INSERT INTO perguntas_avaliacoes (titulo, descricao) VALUES 
    ('Planejamento do atendimento', 'Revisa e sumaria o prontuário focalizando nas necessidades do paciente.'),
    ('História clínica', 'Favorece o relato do contexto de vida do paciente e obtém dados relevantes da história clínica de maneira articulada e cronologicamente adequada.'),
    ('Exame clínico', 'Respeita a privacidade e cuida do conforto do paciente, explica e orienta o paciente sobre os procedimentos a serem realizados; adota medidas de biossegurança.'),
    ('Formulação do problema do paciente', 'Integra e organiza os dados da história e exame clínicos, elaborando hipóteses diagnósticas fundamentadas nos processos de produção da doença.'),
    ('Investigação diagnóstica', 'Solicita e interpreta recursos complementares para confirmar ou afastar as hipóteses elaboradas (exames, visita domiciliária, obtenção de dados com familiares, cuidador ou outros profissionais).'),
    ('Plano de cuidado', 'Elabora um plano de cuidado e terapêutico considerando as evidências encontradas na literatura e o contexto de vida do paciente; envolve outros profissionais ou recursos comunitários quando necessário; contempla ações de prevenção das doenças; considera o grau de resolutividade dos diferentes serviços de atenção à saúde ao referenciar/contra referenciar o paciente.'),
    ('Comunicação, organização e registro de informações', 'Comunica e registra informações relevantes, de forma organizada e orientada para o problema do paciente.'),
    ('Relacionamento interpessoal', 'Mantém comunicação respeitosa com o paciente, com sua família e acompanhante, relacionando-se de maneira empática; estabelece relação de colaboração com colegas e/ou membros de equipe; Faz e recebe críticas respeitosamente.'),
    ('Qualidade do cuidado', 'Avalia indicadores de qualidade do serviço de saúde no qual participa (média de permanência, taxa de infecção hospitalar, etc) e propõe ações de melhoria.'),
    ('Atitude profissional', 'Mostra assiduidade e responsabilidade no cumprimento das tarefas; respeita normas institucionais, posiciona-se ética e humanisticamente diante da profissão.');
